import React from 'react';
import { Formik, Form, Field } from 'formik';
import * as Yup from 'yup';
import axios from "axios";

const SignupSchema = Yup.object().shape({
    email: Yup.string()
        .email('Invalid email')
        .required('Đây là trường bắt buộc!'),
});


const Views = ({history}) => (
    <div>
        <h3 className="text-center"> Sử dụng email đăng ký và mã đơn hàng để xem lại đơn hàng của bạn</h3>
        <Formik
            initialValues={{
                email: '',
                vcode: ''
            }}
            validationSchema={SignupSchema}

            onSubmit={(values, { setSubmitting }) => {
                axios.post('http://localhost:3001/hoadon',values).then(function (response) {
                    console.log(response.data);
                    if(response.data !== 0){
                        history.push(`/yourinvoice`)
                    }
                })
                    .catch(function (error) {
                        console.log(error);
                    });
            }}
        >
            {({ errors, touched }) => (
                <Form>
                    <Field name="email" type="email"  placeholder="Email"/>
                    {errors.email && touched.email ? <div>{errors.email}</div> : null}
                    <Field name="vcode" placeholder="Mã đơn hàng"/>
                    {errors.vcode && touched.vcode ? (
                        <div>{errors.vcode}</div>
                    ) : null}
                    <button type="submit">Gửi đi</button>
                </Form>
            )}
        </Formik>
    </div>
);
export default Views;
