import React, {Component} from 'react';
import Hosting from "./Hosting";
import axios from "axios";
const  getHostdata = () =>{
        return axios.get('http://localhost:3000')
            .then((res) => res.data)
};
class Hostings extends Component {
    constructor(props){
        super(props);
        this.state = {
            data:null
        }
    }
componentWillMount() {
        if (this.state.data ===null){
            getHostdata().then((res) =>{
                this.setState({
                    data:res
                });
            })
        }
}

printout = () => {
  if(this.state.data !== null){
      return this.state.data.map((value,key)=>(
          <Hosting key={key} icongoi="fa-paper-plane" tengoi={value.tengoi} giahost={value.giahost} bt={value.bt} db={value.db} email={value.email} uptime={value.uptime} hotro="fa-times-circle"/> )
      )}

};

    // componentDidMount() {
    //     axios.get('http://localhost:3001').then(res =>{
    //         this.setState({
    //             data:res.data
    //         });
    //         console.log(this.state)
    //     });
    // }
    render() {
        return (
            <div className="container-fluid" data-aos="flip-right">
                <div className="container">
                    <div className="row">
                        {this.printout()}
                        {/*{*/}
                            {/*this.state.data.map((e,i)=>{*/}
                                {/*return(*/}
                                    {/*<Hosting key={i} icongoi="fa-paper-plane" tengoi={e.tengoi} giahost={e.giahost} bt={e.bt} db={e.db} email={e.email} uptime={e.uptime} hotro="fa-times-circle"/>*/}
                                {/*)*/}
                            {/*})*/}
                        {/*}*/}


                        {/*<Hosting icongoi="fa-rocket" tengoi="Pro" giahost="359000" bt="Không giới hạn" db="Không giới hạn" email="Không giới hạn" uptime="99%" hotro="fa-check-circle"/>*/}
                        {/*<Hosting icongoi="fa-plane-departure" tengoi="Medium" giahost="159000" bt="Không giới hạn" db="3" email="20" uptime="99%" hotro="fa-check-circle"/>*/}
                    </div>
                </div>
            </div>
        );
    }
}

export default Hostings;
