import React from 'react';
import { Formik, Form, Field } from 'formik';
import * as Yup from 'yup';
import axios from "axios";

const SignupSchema = Yup.object().shape({
    fullname: Yup.string()
        .min(5, 'Vui lòng nhập tên thật!')
        .max(30, 'Vui nhập lại đúng tên!')
        .required('Đây là trường bắt buộc!'),
    address: Yup.string()
        .min(20, 'Vui lòng nhập địa chỉ cụ thể hơn!')
        .max(80, 'Đây không phải một địa chỉ hợp lệ!')
        .required('Đây là trường bắt buộc!'),
    phonenumber: Yup.number()
        .min(10)
        .max(10)
        .required('Đây là trường bắt buộc!'),
    email: Yup.string()
        .email('Invalid email')
        .required('Đây là trường bắt buộc!'),
});


const Valiform = () => (
    <div>
        <h1> Vui lòng điền đầy đủ thông tin vào các trường sau </h1>
        <Formik
            initialValues={{
                fullname: '',
                address: '',
                phonenumber: '',
                email: '',
            }}
            validationSchema={SignupSchema}

                onSubmit={(values, { setSubmitting }) => {
                    axios.post('http://localhost:3001/mail',values).then(res=>{
                        console.log(res);
                        setSubmitting(false);
                    }).catch(err=>console.log(err));

            }}
        >
            {({ errors, touched }) => (
                <Form>
                    <Field name="fullname" placeholder="Tên"/>
                    {errors.fullname && touched.fullname ? (
                        <div>{errors.fullname}</div>
                    ) : null}
                    <Field name="address"  placeholder=" Địa chỉ"  />
                    {errors.address && touched.address ? (
                        <div>{errors.address}</div>
                    ) : null}
                    <Field name="phonenumber"  placeholder=" Số điện thoại"  />
                    {errors.phonenumber && touched.phonenumber ? (
                        <div>{errors.phonenumber}</div>
                    ) : null}
                    <Field name="email" type="email"  placeholder="Email" />
                    {errors.email && touched.email ? <div>{errors.email}</div> : null}
                    <button type="submit">Gửi đi</button>
                </Form>
            )}
        </Formik>
    </div>
);
export default Valiform;
