import React from 'react';
import { Formik, Form, Field } from 'formik';
import * as Yup from 'yup';
import axios from "axios";

const SignupSchema = Yup.object().shape({
    email: Yup.string()
        .email('Invalid email')
        .required('Đây là trường bắt buộc!'),
});


const Views = ({history}) => (
    <div>
        <h3 className="text-center"> Sử dụng email đăng ký và mã đơn hàng để xem lại đơn hàng của bạn</h3>
        <Formik
            initialValues={{
                email: '',
                vcode: ''
            }}
            validationSchema={SignupSchema}

            onSubmit={(values, { setSubmitting }) => {
                        history.push(`/yourinvoice?email=${values.email}&vcode=${values.vcode}`)
            }}
        >
            {({ errors, touched }) => (
                <Form>
                    <Field name="email" type="email"  placeholder="Email"/>
                    {errors.email && touched.email ? <div>{errors.email}</div> : null}
                    <Field name="vcode" placeholder="Mã đơn hàng"/>
                    {errors.vcode && touched.vcode ? (
                        <div>{errors.vcode}</div>
                    ) : null}
                    <button type="submit">Gửi đi</button>
                </Form>
            )}
        </Formik>
        <p className="text-center"><strong>Lưu ý:</strong> Sau khi thanh toán, khách hàng có thời hạn 7 ngày để huỷ hoá đơn, sau 7 ngày hoá đơn không thể huỷ</p>
        <p className="text-center">Số tiền trừ phí huỷ hoá đơn tính từ ngày tạo hoá đơn đến ngày huỷ hoá đơn hệ số với 3% với tổng giá tiền dịch vụ </p>
        <p className="text-center"> Tổng tiền trừ tối đa: <strong>20%</strong></p>
    </div>
);
export default Views;
