import React, {Component} from 'react';
import axios from "axios";

class Invoice extends Component {
    constructor(props){
        super(props);
        this.state = {
            data:[]
        }
    }

    // lấy dữ liệu từ datahost
    componentDidMount() {
        axios.get('http://localhost:3001/hoadon').then(res =>{
            this.setState({
                data:res.data
            });
            // console.log(this.state)
        });
    }
    render() {
        return (
            <div className="container">
                <h4 className="text-center hdct">Hoá Đơn Chi Tiết</h4>
                <div className="row">
                    {
                        this.state.data.map((value,key) => {
                                return(
                                    <table key={key} className="table table-hover" id="dev-table">
                                        <tbody>
                                        <tr><th>Tên khách hàng: </th>
                                            <td>{value.tenkhach}</td>
                                        </tr>
                                        <tr>
                                            <th>Email:</th>
                                            <td>{value.email}</td>
                                        </tr>
                                        <tr>
                                            <th>Số điện thoại: </th>
                                            <td>{value.sdtkh}</td>
                                        </tr>
                                        <tr>
                                            <th>Địa chỉ: </th>
                                            <td>{value.diachi}</td>
                                        </tr>
                                        <tr>
                                            <th>Số tài khoản:</th>
                                            <td>{value.sotkkh}</td>
                                        </tr>
                                        <tr>
                                            <th>Tên dịch vụ:</th>
                                            <td>Template</td>
                                        </tr><tr>
                                            <th>Gói đăng ký:</th>
                                            <td>EBUSSINESS</td>
                                        </tr><tr>
                                            <th>Giá: </th>
                                            <td>2590k</td>
                                        </tr><tr>
                                            <th>Tình trạng thanh toán: </th>
                                            <td>Đã thanh toán</td>
                                        </tr>
                                        </tbody>
                                    </table>
                                )

                        })
                    }
                </div>
            </div>
        );

    }
}

export default Invoice;
