import React from 'react';
import { Formik } from 'formik';
import axios from 'axios';

const Form = () => (

    <div className="container ">
        <Formik

            initialValues={{ email: ''}}
            validate={values => {
            let errors = {};
            if (!values.email) {
                errors.email = 'Trường bắt buộc';
            } else if (
                !/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(values.email)
            ) {
                errors.email = 'Địa chỉ gmail không đúng!';
            }
            return errors;
        }}


            onSubmit={(values, { setSubmitting }) => {
                axios.post('http://localhost:3001/mail',values).then(res=>{
                    console.log(res);
                    setSubmitting(false);
                }).catch(err=>console.log(err));
            }}
        >
            {({
                  values,
                  errors,
                  touched,
                  handleChange,
                  handleBlur,
                  handleSubmit,
                  isSubmitting,
                  /* and other goodies */
              }) => (
                <form onSubmit={handleSubmit}>
                    <input
                        type="email"
                        name="email"
                        onChange={handleChange}
                        onBlur={handleBlur}
                        value={values.email}
                    />
                    {errors.email && touched.email && errors.email}
                    <input
                        type="name"
                        name="name"
                        onChange={handleChange}
                        onBlur={handleBlur}
                        value={values.name}
                    />

                    <button type="submit" disabled={isSubmitting}>
                        Gửi đi
                    </button>
                </form>
            )}
        </Formik>
    </div>
);

export default Form;
