import React, { Component } from 'react';
import Form from 'react-validation/build/form';
import Input from 'react-validation/build/input';
import CheckButton from 'react-validation/build/button';
import { isEmail, isEmpty } from 'validator';
import 'react-phone-number-input/style.css'
import PhoneInput from 'react-phone-number-input'
const minname = (value) => {
    if (value.trim().length < 3) {
        return <small className="form-text text-danger">Tên của bạn không hợp lệ!</small>;
    }
};
const maxname = (value) => {
    if (value.trim().length < 30) {
        return <small className="form-text text-danger">Tên của bạn không hợp lệ!</small>;
    }
};
const required = (value) => {
    if (isEmpty(value)) {
        return <small className="form-text text-danger">Đây là trường bắt buộc!</small>;
    }
};

const email = (value) => {
    if (!isEmail(value)) {
        return <small className="form-text text-danger">Bạn nhập không đúng định dạng!</small>;
    }
};

const minaddress = (value) => {
    if (value.trim().length < 30) {
        return <small className="form-text text-danger">Vui lòng mô tả địa chỉ chi tiết hơn</small>;
    }
};
const minphone = (value) => {
    if (value.trim().length < 10) {
        return <small className="form-text text-danger">Vui lòng kiểm tra lại số điện thoại</small>;
    }
};
const maxphone = (value) => {
    if (value.trim().length > 10) {
        return <small className="form-text text-danger">Bạn đã nhập thừa số điện thoại</small>;
    }
};
const minvalue = (value) => {
    if (value.trim().length < 12) {
        return <small className="form-text text-danger">Bạn nhập thiếu số TK. Vui lòng xem lại!</small>;
    }
};
const maxvalue = (value) => {
    if (value.trim().length > 15) {
        return <small className="form-text text-danger">Số tài khoản không chính xác!</small>;
    }
};
class Login extends Component {

    constructor(props){
        super(props);
        this.state = {
            yourname : '',
            email : '',
            phonenumber : '',
            address : '',

        };
    }

    onSubmit(e){
        e.preventDefault();
        this.form.validateAll();

        if ( this.checkBtn.context._errors.length === 0 ) {
            alert('success');
        }
    }

    render() {
        return (
            <div className="container">
                <div className="login-container">
                    <div id="output"></div>
                    <div className="avatar"></div>
                    <div className="form-box">
                        <Form onSubmit={e => this.onSubmit(e)} ref={c => { this.form = c }}>
                            <Input
                                name="yourname"
                                onChange={this.onChangeHandler}
                                type="text"
                                placeholder="Tên đầy đủ"
                                className="form-control"
                                validations={[required,minname, maxname]}
                            />
                            <Input
                                name="email"
                                onChange={this.onChangeHandler}
                                type="text"
                                placeholder="Email"
                                className="form-control"
                                validations={[required, email]}
                            />
                            {/*<Input*/}
                                {/*name="phonenumber"*/}
                                {/*onChange={this.onChangeHandler}*/}
                                {/*type="number"*/}
                                {/*placeholder="Số điện thoại"*/}
                                {/*className="form-control"*/}
                                {/*validations={[required, minphone, maxphone]}*/}
                            {/*/>*/}
                            <PhoneInput
                                placeholder="Enter phone number"
                                value={ this.state.phone }
                                onChange={ phone => this.setState({ phone }) } />
                            <Input
                            name="address"
                            onChange={this.onChangeHandler}
                            type="text"
                            placeholder="Địa chỉ"
                            className="form-control"
                            validations={[required, minaddress]}
                        />
                            <Input
                                name="atm"
                                onChange={this.onChangeHandler}
                                type="text"
                                placeholder="Số tài khoản"
                                className="form-control"
                                validations={[required, minvalue, maxvalue]}
                            />
                            <button className="btn btn-info btn-block login" type="submit">Login</button>
                            <CheckButton style={{ display: 'none' }} ref={c => { this.checkBtn = c }} />
                        </Form>
                    </div>
                </div>
            </div>
        );
    }
}

export default Login;
